import React from "react";
import "./Nav.scss";
import { Button } from "antd";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import AskQuestion from "../askQuestion/AskQuestion";
import { connect } from "react-redux";
import {logoutUser} from "../../redux/index"
function Nav(props) {
  const renderButtons = () => {
    if (cookie.load("token")) {
        console.log(props)
      return (
        <div className="nav__buttons">
          <AskQuestion {...props}>Ask Question</AskQuestion>
          <Button onClick={() => props.logoutUser(props.history)}>Logout</Button>
        </div>
      );
    }
    return (
        <div className="nav__buttons">
        <Link to="/login">
          <Button  type="primary">
            Sign In
          </Button>
        </Link>
        <Link to="/register">
          <Button >Register</Button>
        </Link>
      </div>
    )
  };
  return (
    <div className="nav">
      <div>
        <Link to="/">Stack Overflow Clone</Link>
      </div>

     {renderButtons()}
    </div>
  );
}

const mapStateToProps = state => ({
    questions: state.question.questions,
    questionsLoader: state.question.questionsLoader
  });

  const mapDispatchToProps = dispatch => {
    return {
        logoutUser: (history) => dispatch(logoutUser(history))
    };
  };
  export default connect(mapStateToProps, mapDispatchToProps)(Nav);
