import React, { useEffect } from "react";
import { Form, Icon, Input, Button, Checkbox } from "antd";
import {  registerUser } from "../redux/index";
import { connect } from "react-redux";
import { message } from "antd";
import cookie from "react-cookies";
import Router from "next/router";
import Layout from "../components/Layout";

function RegisterForm(props) {
  useEffect(() => {
    if (cookie.load("token")) {
      return Router.push("/");
    }
  }, []);

  const handleSubmit = e => {
    e.preventDefault();
    props.form.validateFields(async (err, values) => {
      if (!err) {
        await props.registerUser(values, props.history, message);
      }
    });
  };
  const { getFieldDecorator } = props.form;
  return (
    <Layout>
      <div className="login__form-container">
        <Form onSubmit={handleSubmit} className="login-form">
          <Form.Item>
            {getFieldDecorator("displayName", {
              rules: [
                { required: true, message: "Please input your display name!" }
              ]
            })(
              <Input
                prefix={
                  <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                }
                placeholder="Dispaly Name"
              />
            )}
          </Form.Item>
          <Form.Item>
            {getFieldDecorator("email", {
              rules: [{ required: true, message: "Please input your email!" }]
            })(
              <Input
                prefix={
                  <Icon type="mail" style={{ color: "rgba(0,0,0,.25)" }} />
                }
                placeholder="Email"
              />
            )}
          </Form.Item>
          <Form.Item>
            {getFieldDecorator("password", {
              rules: [
                { required: true, message: "Please input your Password!" }
              ]
            })(
              <Input
                prefix={
                  <Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />
                }
                type="password"
                placeholder="Password"
              />
            )}
          </Form.Item>
          <Form.Item>
            <Button
              type="primary"
              loading={props.registerLoader}
              htmlType="submit"
              className="login-form-button"
            >
              Register
            </Button>
          </Form.Item>
        </Form>
      </div>
    </Layout>
  );
}
const WrappedRegisterForm = Form.create({ name: "register_form" })(
  RegisterForm
);

const mapStateToProps = state => ({
  registerLoader: state.user.registerLoader
});

const mapDispatchToProps = dispatch => {
  return {
    registerUser: (data, history, message) =>
      registerUser(data, history, message)(dispatch)
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(WrappedRegisterForm);
